import React, { useState } from "react";
import { secondsToDuration, minutesToDuration } from "../../utils/duration";
import isTimerRunning from "../Pomodoro";

const Focus = () => {
  const [focusLength, setFocusLength] = useState(25);

  const decrementFocusLengthByOneMinute = () => {
    setFocusLength((lastFocus) => lastFocus - 1); // take the previous focus duration and subtract
  };

  const incrementFocusLengthByOneMinute = () => {
    setFocusLength((lastFocus) => lastFocus + 1);
  };

  return (
    <div className="col">
      <div className="input-group input-group-lg mb-2">
        <span className="input-group-text" data-testid="duration-focus">
          {/* TODO: Update this text to display the current focus session duration */}
          <p id="break-label">
            Focus Duration: {minutesToDuration(focusLength)}
          </p>
        </span>
        <div className="input-group-append">
          {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
          <button
            type="button"
            className="btn btn-secondary"
            data-testid="decrease-focus"
            onClick={decrementFocusLengthByOneMinute}
            disabled={focusLength <= 5 || isTimerRunning ? true : false}
            //
          >
            <span className="oi oi-minus" />
          </button>
          {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
          <button
            type="button"
            className="btn btn-secondary"
            data-testid="increase-focus"
            onClick={incrementFocusLengthByOneMinute}
            disabled={focusLength >= 60 || isTimerRunning ? true : false}
            //
          >
            <span className="oi oi-plus" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Focus;
