import React, { useState } from "react";
import { secondsToDuration } from "../../utils/duration";

const Session = () => {
  const [sessionLength, setSessionLength] = useState(60 * 25);

  const decrementSessionLengthByOneMinute = () => {
    const newSessionLength = sessionLength - 60;

    if (newSessionLength < 60 * 5) {
      setSessionLength(60 * 5);
    } else if (newSessionLength > 60 * 60) {
      setSessionLength(60 * 60);
    } else {
      setSessionLength(newSessionLength);
    }
  };

  const incrementSessionLengthByOneMinute = () => {
    setSessionLength(sessionLength + 60);
  };

  function sessionLengthInMinutesFunc(time) {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60; // get the remainder
    return `${minutes}:${seconds}0`;
  }
  const sessionLengthInMinutes = secondsToDuration(sessionLength);

  return (
    <div className="col">
      <div className="input-group input-group-lg mb-2">
        <span className="input-group-text" data-testid="duration-focus">
          {/* TODO: Update this text to display the current focus session duration */}
          <p id="break-label">Focus Duration: {sessionLengthInMinutes}</p>
        </span>
        <div className="input-group-append">
          {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
          <button
            type="button"
            className="btn btn-secondary"
            data-testid="decrease-focus"
            onClick={decrementSessionLengthByOneMinute}
          >
            <span className="oi oi-minus" />
          </button>
          {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
          <button
            type="button"
            className="btn btn-secondary"
            data-testid="increase-focus"
            onClick={incrementSessionLengthByOneMinute}
          >
            <span className="oi oi-plus" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Session;
