import React, { useState } from "react";
import { secondsToDuration, minutesToDuration } from "../../utils/duration";
//import Pomodoro from "../Pomodoro";

const Break = ({ isTimerRunning, isSessionActive }) => {
  const decrementBreakLengthByOneMinute = () => {
    setBreakLength((lastBreak) => Math.max(1, lastBreak - 1)); // take the previous focus duration and subtract
  };

  const incrementBreakLengthByOneMinute = () => {
    setBreakLength((lastBreak) => Math.min(15, lastBreak + 1));
  };

  return (
    <div className="col">
      <div className="float-right">
        <div className="input-group input-group-lg mb-2">
          <span className="input-group-text" data-testid="duration-break">
            {/* TODO: Update this text to display the current break session duration. MUST USE minutesToDuration. */}
            <p id="break-label">
              Break Duration: {minutesToDuration(breakLength)}
            </p>
            {/* <p id="break-length">{breakLength}</p> */}
          </span>
          <div className="input-group-append">
            {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="decrease-break"
              onClick={decrementBreakLengthByOneMinute}
              disabled={isSessionActive || isTimerRunning ? true : false}
              //
            >
              <span className="oi oi-minus" />
            </button>
            {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="increase-break"
              onClick={incrementBreakLengthByOneMinute}
              disabled={isSessionActive || isTimerRunning ? true : false}
              //
            >
              <span className="oi oi-plus" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Break;
